extern crate image;
extern crate base64;

use base64::decode;
use image::{DynamicImage, GenericImageView, ImageFormat};
use image::imageops::grayscale;
use wasm_bindgen::prelude::*;
use web_sys::window;
use wasm_bindgen::JsCast;

const IMG: &str = "";

#[wasm_bindgen]
pub fn convert(name: &str) {
    let greetings = format!("Hello, {}!", name);

    let window = window().unwrap();
    let document = window.document().unwrap();
    let target = document.get_element_by_id("target").expect("document should have a target element");

    let img = read_img(IMG);
    let dim = img.dimensions();

    let img_element = document.create_element("img")
    .unwrap()
    .dyn_into::<web_sys::HtmlImageElement>()
    .unwrap();
    img_element.set_alt(&greetings);
    img_element.set_name("output");
    img_element.set_width(dim.0);
    img_element.set_height(dim.1);

    target.append_child(&img_element).unwrap();
}

fn read_img(data: &str) -> DynamicImage {
    let vec = decode(data).unwrap();
    let buf = vec.as_slice();
    return image::load_from_memory_with_format(buf, ImageFormat::Png).unwrap();
}
